import React from "react";

function Main() {
  return (
    <main className="main">
      <section className="profile">
        <div className="profile__avatar-container">
          <img
            src="<%=require('./images/Jak_Iv_Kusto.jpg')%>"
            className="profile__avatar"
            alt="Аватар профиля"
          />
        </div>
        <div className="profile__container">
          <div className="profile__info">
            <h1 className="profile__name">Влад</h1>
            <button
              type="button"
              className="profile__editButton button-hover"
            ></button>
          </div>
          <p className="profile__profession">Портной</p>
        </div>
        <div>
          <button
            type="button"
            className="profile__addButton button-hover"
          ></button>
        </div>
      </section>
      <section className="places" aria-label="Фото красивых мест">
        <ul className="card-grid"></ul>
      </section>
    </main>
  );
}

const modalActiveClass = document.querySelector("modal_active");
const handleEditAvatarClick = document.querySelector(".profile__avatar");
const handleEditProfileClick = document.querySelector(".profile__editButton");
const handleAddCardClick = document.querySelector(".profile__addButton");

const popupEditAvatar = document.querySelector(".modal-avatar");
const popupEditProfile = document.querySelector(".modal-edit");
const popupAddCard = document.querySelector(".modal-add");

console.log(handleAddCardClick);

handleEditAvatarClick.addEventListener("click", () => {
  popupAddCard.classList.add(modalActiveClass);
});

handleEditProfileClick.addEventListener("click", () => {
  popupEditProfile.classList.add(modalActiveClass);
});

handleAddCardClick.addEventListener("click", () => {
  popupEditAvatar.classList.add(modalActiveClass);
});

export default Main;
